bl,#paramscol1Width,#paramscol2Width-lbl,#paramscol2Width").show();}							if(column3) {jQuery("#paramscol1Width-lbl,#paramscol1Width,#paramscol3Width-lbl,#paramscol3Width").show();}							if(column4) {jQuery("#paramscol1Width-lbl,#paramscol1Width,#paramscol4Width-lbl,#paramscol4Width").show();}														switch(jQuery('#paramsmasonryWidths :selected').text()) {							case 'Use widths specified in meta keywords':								jQuery("#paramsmasonryColumnWidth-lbl,#paramsmasonryColumnWidth,#paramsmasonryGutter-lbl,#paramsmasonryGutter,#paramsmasonryColWidths-lbl,#paramsmasonryColWidths").show();								jQuery("#paramsmasonryColWidths-lbl,#paramsmasonryColWidths").hide();							break;							case 'Equalise widths of all elements':														jQuery("#paramsmasonryColumnWidth-lbl,#paramsmasonryColumnWidth,#paramsmasonryGutter-lbl,#paramsmasonryGutter,#paramsmasonryColWidths-lbl,#paramsmasonryColWidths").hide();							jQuery("#paramsmasonryColWidths-lbl,#paramsmasonryColWidths").show();							break;							}														// Toggle for k2 content source							switch(jQuery('#paramsk2contentSource :selected').text()) {								case 'Items':									jQuery("#paramsitemid,#paramsitemid-lbl").show();									jQuery("#paramscategory_id,#paramscategory_id-lbl,#paramsgetChildren,#paramsgetChildren-lbl").hide();								break;								case 'Categories':									jQuery("#paramsitemid,#paramsitemid-lbl").hide();									jQuery("#paramscategory_id,paramscategory_id-lbl,#paramsgetChildren,#paramsgetChildren-lbl").show();								break;							}																					// toggle for the k2 image option							if(k2source && hasImage ) {												jQuery("#paramsitemImgSize,#paramsitemImgSize-lbl").show();							}							else {								jQuery("#paramsitemImgSize,#paramsitemImgSize-lbl").hide();							}															// Retrieve the cookie contents for this instance of the module							var itemOrder = jQuery("#paramsuseditems").val();							});														