rst!  You can modify this									// to support multiple lists - not tested!									jQuery("ul.ui-sortable").filter(":first").append(savedOrd);																										}							}						}						// code executed when the document loads						jQuery(function() {							// here, we allow the user to sort the items							jQuery(setSelector).sortable({									connectWith: ".connectedLists",									cursor: "move",									placeholder: "ui-state-highlight",									scope: "tags",									opacity: 0.8,									dropOnEmpty: true,									items: "li:not(.disabled)",									revert: 200,									update: function(event,ui) { 																				jQuery("#zenmessage p").html("").show();										jQuery("#zenmessage p").html("Settings updated").delay(600).fadeOut(300);																					// Variables for sortable list elements											var column2 = 0;											var column3 = 0;											var column4 = 0;																						// Hides text related panels if image isnt in the ordering											if(jQuery("#sortable li#text").length == 1)  	{	var hasText = 1;}											if(jQuery("#sortable li#date").length == 1)  	{  	var hasDate = 1;}											if(jQuery("#sortable li#image").length == 1)  	{	var hasImage = 1;}											if(jQuery("#sortable li#title").length == 1)  	{	var hasTitle= 1;}											if(jQuery("#sortable li#more").length == 1)  	{	var hasMore = 1;}											if(jQuery("#sortable li#column2").length == 1)  {	var column2 = 1;}											if(jQuery("#sortable li#column3").length == 1)  {	var column3 = 1;}											if(jQuery("#sortable li#column4").length == 1)  {	var column4 = 1;}																																	if(hasText) {jQuery("#panelText").show();} else { jQuery("#panelText").hide();} 											if(hasTitle) {jQuery("#panelTitle").show();} else { jQuery("#panelTitle").hide();} 											if(hasDate) {jQuery("#panelDate").show();} else { jQuery("#panelDate").hide();} 											if(hasMore) {jQuery("#panelReadmore").show();